package com.boring.dal.cache.config;

public class MemSvrConfig {
    private String addr;
    private Integer port;

    public String getAddr() {
        return addr;
    }

    public void setAddr(String addr) {
        this.addr = addr;
    }

    public Integer getPort() {
        return port;
    }

    public void setPort(Integer port) {
        this.port = port;
    }
}
